import React, { useEffect, useState } from 'react'
import { useParams, Link } from 'react-router-dom'
import axios from 'axios'
function Categore() {
  // console.log("Aaa");
  const { categore } = useParams()
  const [isLoded, setIsLoded] = useState(false)
  const [data, setData] = useState([])
  const [rate] = useState([1, 2, 3, 4, 5])
  useEffect(() => {
    axios
      .get(`https://fakestoreapi.com/products/category/${categore}`)
      .then(res => {
        setData(res.data)
        setIsLoded(true)
      })

    return () => {
      setIsLoded(false)
    }
  }, [])

  return (
    <>
      <div className="container mt-5">
        <div className="row">
          {data.map(data => {
            return (
              <div className="  col-md-6 col-lg-4  col-xl-3 mt-4">
                <div className="card" key={data.id}>
                  <img style={{ height: "280px" }} className='card-head  rounded  p-3' src={data.image} alt="404" />
                  <div className="card-body">
                    <h5 className='card-title'>{data.title}</h5>
                    <p className='card-text'>{data.description}</p>
                    <Link className='nav-link' to={`${data.id}`}>
                            <button
                                type="button"
                                className="btn  btn-outline-secondary"
                            >Buy</button>
                        </Link>
                  </div>
                </div>
              </div>
            )
          })

          }
        </div>
      </div>
    </>
  )
}

export default Categore
