import { useDispatch, useSelector } from 'react-redux'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { removeProduct, setSelectorProdact } from '../redux/reducers/selectProdact';

function ProductDetails() {
    const selectrProduct = useSelector(state => state.selectProduct.obj)
    const dispatch = useDispatch();
    const { productId } = useParams()
    const [isLoded,setIsLoded]= useState(false);
    useEffect(() => {
        axios
            .get(`https://fakestoreapi.com/products/${productId}`)
            .then(res => {
                dispatch(setSelectorProdact(res.data))
                setIsLoded(true)
            })
            return ()=>{
                dispatch(removeProduct())
                setIsLoded(false)
            }
    },[])
    return (
        <>
            {isLoded?
                (<h1>{selectrProduct.title}</h1>)
                :
                (<h1 style={{position:'absolute',top:0,left:0,r}} className='w-100 h-100 d-flex'>
                    <i className='bx bx-loader-alt bx-spin bx-flip-horizontal' ></i>
                </h1>)
            }
        </>
    )
}

export default ProductDetails
