import React, { useEffect, useState } from 'react'
import { Link,BrowserRouter,Routes } from "react-router-dom"
import axios from 'axios'
import { useSelector } from 'react-redux'
function Categores() {
  const [categories, setCategories] = useState([])
  const [isLoded, setIsLoded] = useState(false)
  const allProducts = useSelector(state => state.product.array)
  useEffect(() => {
    axios
      .get(`https://fakestoreapi.com/products/categories`)
      .then(res => {
        setCategories(res.data);
        setIsLoded(true)
      })

    return () => {
      setIsLoded(false)
    }
  }, [])


  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route>
          {isLoded ? (<div style={{ marginTop: "70px" }} className="container ">
            <div className="row">
              <div className="col-xl-3"></div>
              <div className="col-xl-6">
                <table className='table table-hover'>
                  <thead>
                    <tr>
                      <th className='col'>Categores</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      categories.map(element => {
                        return < tr>
                          <Link onClick={() => setIsLoded(true)} className='nav-link col' to={`${element}`}>{element}</Link>
                        </tr>
                      })
                    }
                  </tbody>
                </table>
              </div>
            </div>
          </div>)
            : (<h1 style={{ height: "80vh" }} className='w-100'>
              <i style={{ position: "fixed", top: "45vh", left: "50vw", zIndex: 12, fontSize: "100px" }} className='bx bx-loader bx-spin bx-flip-horizontal' ></i>
            </h1>)
          }
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  )
}

export default Categores
