import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
function Categore() {
  // console.log("Aaa");
  const { categore } = useParams()
  console.log(categore);
  const [isLoded, setIsLoded] = useState(false)
  const [data, setData] = useState([])

  useEffect(() => {
    axios
      .get(`https://fakestoreapi.com/products/categories/${categore}`)
      .then(res => {
        setData(res.data)
        setIsLoded(true)
      })

    return () => {
      setIsLoded(false)
    }
  }, [])
  return (
    <>
      {isLoded ? (<div style={{ marginTop: "70px" }} className="container ">
        <div className="row">
          {
            data.map(element => {
              return (
                <div className="col-lg-4 col-md-6 mb-4" key={element.id}>
                  <div className="card">
                    <div className="card-body">
                      <h5 className="card-title">{element.name}</h5>
                      <p className="card-text">{element.description}</p>
                    </div>
                  </div>
                </div>
              )
          })
        }
      </div>
      </div>)
        : (<h1 style={{ height: "80vh" }} className='w-100'>
    <i style={{ position: "fixed", top: "45vh", left: "50vw", zIndex: 12, fontSize: "100px" }} className='bx bx-loader bx-spin bx-flip-horizontal' ></i>
  </h1>)
}
    
        </>
    )
}

export default Categore
