import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { setProducts } from '../redux/reducers/allProdactReducer';
import ProductCard from '../components/ProductCard';
function AllProducts() {
    const allProducts = useSelector(state => state.product.array)
    // console.log(allProducts);
    const dispatch = useDispatch();
    const [isLoded, setIsLoded] = useState(false);
    const [sortBy, setSort] = useState(false);
    useEffect(() => {
        axios
            .get(`https://fakestoreapi.com/products?sort=${sortBy ? 'desc' : ''}`)
            .then((res) => {
                dispatch(setProducts(res.data))
                setIsLoded(true)
            })
        return () => {
            setIsLoded(false)
        }
    }, [])

    return (
        <>
            {isLoded ?
                (<div className="container mt-5">
                    <div className="row">
                        <div className="pt-4 d-flex justify-content-end">
                            <div class="form-check form-switch">
                                <input onChange={setSort(!sort)} class="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
                                    <label class="form-check-label" for="flexSwitchCheckDefault">Sort</label>
                            </div>
                        </div>
                        <>
                            {
                                allProducts.map(allProduct => {
                                    return <ProductCard key={allProduct.id} data={allProduct} />
                                })
                            }
                        </>
                    </div>
                </div>)
                :
                (<h1 style={{ height: "80vh" }} className='w-100'>
                    <i style={{ position: "fixed", top: "45vh", left: "50vw", zIndex: 12, fontSize: "100px" }} className='bx bx-loader bx-spin bx-flip-horizontal' ></i>
                </h1>)
            }
        </>
    )
}

export default AllProducts
