import React, { useEffect, useState } from 'react'
import { useParams, Link } from 'react-router-dom'
import axios from 'axios'
function Categore() {
  // console.log("Aaa");
  const { categore } = useParams()
  const [isLoded, setIsLoded] = useState(false)
  const [data, setData] = useState([])
  const [rate] = useState([1, 2, 3, 4, 5])
  useEffect(() => {
    axios
      .get(`https://fakestoreapi.com/products/category/${categore}`)
      .then(res => {
        setData(res.data)
        setIsLoded(true)
      })

    return () => {
      setIsLoded(false)
    }
  }, [])
  return (
    <>
      {isLoded ? (<div style={{ marginTop: "70px" }} className="container ">
        <div className="row">
          {
            data.map(element => {
              return (
                <div className=" col-md-6 col-lg-4 col-xl-3  mt-4">
                  <div className='card'>
                    <img style={{ height: "280px" }} className='card-head  rounded  p-3' src={data.image} alt="404" />
                    <div className='card-body'>
                      <h5 className='card-title'>Name: {data.category}</h5>
                      <p className='card-text h6'>Price: {data.price}$</p>
                      <div className="d-flex justify-content-between align-items-center">
                        <span className='fs-6 '><i className='bx bx-show-alt'></i> {data.rating.count}</span>
                        <span className='fs-6'>{
                          rate.map((val) => {
                            const num = data.rating.rate - Math.floor(data.rating.rate);
                            let number;
                            number = (num >= 0.5 ? Math.ceil(data.rating.rate) : Math.floor(data.rating.rate));

                            if (val <= number) {
                              return <i style={{ color: '#e6bc19' }} className='bx bxs-star'></i>
                            }
                            else {
                              return <i style={{ color: '#e6bc19' }} className='bx bx-star'></i>
                            }
                          })
                        }
                          {data.rating.rate}
                        </span>

                        <Link className='nav-link' to={`/${data.id}`}>
                          <button
                            type="button"
                            className="btn  btn-outline-secondary"
                          >Buy</button>
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              )
            })
          }
        </div>
      </div>)
        : (<h1 style={{ height: "80vh" }} className='w-100'>
          <i style={{ position: "fixed", top: "45vh", left: "50vw", zIndex: 12, fontSize: "100px" }} className='bx bx-loader bx-spin bx-flip-horizontal' ></i>
        </h1>)
      }

    </>
  )
}

export default Categore
