import { createSlice } from "@reduxjs/toolkit";

const useReducer = createSlice({
 name:'user',
 initialState:{ism:0},
 reducers:{
   changeToToshmat:(state)=>{
     state.ism++
   },
     changeToAnvar: (state) =>{
    state.ism--
  },
  changeToNull:(state)=>{
    state.ism=0
  }
 }
});
export const {changeToToshmat,changeToAnvar,changeToNull}=useReducer.actions
export default useReducer.reducer
